import axios from 'axios';
import {
    LOGIN_USER,
    REGISTER_USER,
    AUTH_USER,
    LOGOUT_USER,
    ADD_TO_CART,
    GET_CART_ITEMS
} from './types';
import { USER_SERVER } from '../components/Config.js';
//import { response } from 'express';  

export function registerUser(dataToSubmit){
    const request = axios.post(`${USER_SERVER}/register`,dataToSubmit)
        .then(response => response.data);

    return {
        type: REGISTER_USER,
        payload: request
    }
}

export function loginUser(dataToSubmit){
    const request = axios.post(`${USER_SERVER}/login`,dataToSubmit)
                .then(response => response.data);

    return {
        type: LOGIN_USER,
        payload: request
    }
}

export function auth(){
    const request = axios.get(`${USER_SERVER}/auth`)
    .then(response => response.data);

    return {
        type: AUTH_USER,
        payload: request
    }
}

export function logoutUser(){
    const request = axios.get(`${USER_SERVER}/logout`)
    .then(response => response.data);

    return {
        type: LOGOUT_USER,
        payload: request
    }
}

export function addToCart(id){

    let body = {
        productId :id   
    }
    //body를넣고 addToCart end point를 backend로(routes/users.js) 보내기(21.9/8)
    const request = axios.post(`${USER_SERVER}/addToCart`,body)
    .then(response => response.data);

    //request를 받아 reducer.js로 가기
    return {
        type: ADD_TO_CART,
        payload: request

    }
}

export function getCartItems(cartItems,userCart){

    const request = axios.get(`/api/product/products_by_id?id=${cartItems}&type=array`)
    .then(response => {
        //routes/product.js에서 request받아와서
        //CartIiem들에 해당하는 정보들을 Product Collection에서 가져온후 qiantity정보를 넣어준다

        userCart.forEach(cartItem => {
            response.data.product.forEach((productDetail, index) => {   //3)redux 간단하게 product.js수정경우 product 없애도 됨
                if(cartItem.id === productDetail._id){
                    response.data.product[index].quantity= cartItem.quantity  //3)redux 간단하게 product.js수정경우 product 없애도 됨
                }
            })
          })
          return response.data;
        //return 후 user_reducer.js 로 가기
    });

    //request를 받아 reducer.js로 가기
    return {
        type: GET_CART_ITEMS,
        payload: request

    }
}
