import React, {useEffect, useState } from 'react'
import {useDispatch} from 'react-redux';
import {getCartItems, removeCartItem, onSuccessBuy} from '../../../_actions/user_actions';
import UserCardBlock from './Sections/UserCardBlock';
import { Empty } from 'antd';
import Paypal from '../../utils/Paypal';


function CartPage(props) {
 
  const dispatch = useDispatch();

  const [Total,setTotal] =useState(0)
  const [ShowTotal,setShowTotal] = useState(false) 
  // true일때만 가격 보여주기
  
  useEffect(() =>{
   
    let cartItems = []

    //redux User state안에 cart안에 상품이 들어있는 지 확인
    if(props.user.userData && props.user.userData.cart){
        if(props.user.userData.cart.length > 0){
          props.user.userData.cart.forEach(item => {
              cartItems.push(item.id)
          })
          dispatch(getCartItems(cartItems, props.user.userData.cart))
            .then(response => {calculateTotal(response.payload)})
       }
    }
  },[props.user.userData])

  let calculateTotal =(cartDetail) =>{
    let total =0;
//Objects, {} in JavaScript does not have the method .map(). It's only for Arrays, [].
//→ 오브젝트는 map()함수를 가지고있지 않아 사용할 수가 없다. 배열만 가능하다.
//→ data.map()으로 썻으면 data.product.map()으로 코드를 바꿔라 왜냐면 product가 배열이니깐 잘 반복될거야

    cartDetail.product.map(item => {
      total += parseInt(item.price, 10) * item.quantity
    })
    setTotal(total)
    setShowTotal(true)
  }

  let removeFromCart = (productId) => {
    //2) redux action =removeCartItem  => user_actions.js의 export function removeCartItem
    dispatch(removeCartItem(productId))
    .then(response =>{
      //console.log('remove버튼 response',response)
      if(response.payload.productInfo.length <=0){
        setShowTotal(false)

      }

    })
  }
    const transactionSuccess = (data) => {
          //자식 컴포넌트에서 넘겨준 payment 정보를 data 매개변수로 받아 처리
      dispatch(onSuccessBuy({       //onSuccessBuy = action, user_actione으로 두개인자 data 보내기
        paymentData : Data,     // paypal 결제 성공시 넘겨받은 paypay 에서 준 payment 정보를 매개변수로 받은 data =payment Data
        cartDetail : props.user.cartDetail     //두번째 전달 인자 cartDetail
      }))
      .then(response => {
        if(response.payload.success){
          setShowTotal(false)     //성공시 카트 비우기

        }
      })
    }

  return (
    <div style={{width:'85%', margin:'3rem auto'}}>
        <h1>My Cart </h1>
        <div>
          <UserCardBlock products={props.user.cartDetail  && props.user.cartDetail.product} removeItem={removeFromCart}/>
          {/* //3)redux 간단하게 product.js수정경우  && props.user.cartDetail.product 없애도 됨 */}
        </div>

        {ShowTotal ?
            //{/* 합계금액 UI만들기 */}
          <div style={{marginTop:'3rem'}}>
            <h2>Total Amount : ${Total}</h2>
          </div>
          : 
          <>
          <br/>
          <br/>
            <Empty description={false}/>
            <br/>
          </>
         
      }
           {/* <p style={{textAlign:'center' ,color:'red'}}>카트에 선택하신 상품이 없습니다</p> */}
            {ShowTotal && 
             <Paypal 
             total = {Total}
             onSuccess={transactionSuccess}
             />
            
            }
    </div>
  )
}

export default CartPage
