import {
    LOGIN_USER,
    REGISTER_USER,
    AUTH_USER,
    LOGOUT_USER,
    ADD_TO_CART
} from '../_actions/types';
import { addToCart } from '../_actions/user_actions';


export default function(state={},action){
    switch(action.type){
        case REGISTER_USER:
            return {...state, register: action.payload }
        case LOGIN_USER:
            return { ...state, loginSucces: action.payload }
        case AUTH_USER:
            return {...state, userData: action.payload }
        case LOGOUT_USER:
            return {...state }
        case ADD_TO_CART:
              return {...state,
                        userData:{
                             ...state.userData,  //원래있던 useData넣기
                             cart:action.payload  //cart넣기
                        }
            }
        default:
            return state;
    }
}
