import React, { useEffect, useState } from 'react'
import { FaCode } from "react-icons/fa";
import axios from "axios";
import {Icon,Col,Card,Row,Carousel} from 'antd'
import Meta from 'antd/lib/card/Meta';
import ImageSlider from '../../utils/ImageSlider';
import CheckBox from './Sections/CheckBox';
import { continents } from './Sections/Datas';



function LandingPage() {

    const [Products, setProducts]  =  useState([])
    const [Skip,setSkip] = useState(0)
    const [Limit,setLimit] = useState(8)
    const [PostSize,setPostSize] = useState(0)
    //Filters  두개의 배열로 초기 state
    const [Filters,setFilters] = useState({
        continents:[],
        price : []
    })


    useEffect(() => {
       //body에 넣어 request로 같이 보내기
        let body = {
            skip:Skip,
            limit:Limit
        }
        //함수화 하여 호출
        getProducts(body)   
    },[])
    const getProducts = (body) =>{
        axios.post('/api/product/products',body)
            .then(response =>{
                if(response.data.success){
                   // console.log(response.data)
                    //원래있는 product를모두 넣고 , 추가의미
                   if(body.loadMore){
                    setProducts([...Products, ...response.data.productInfo]) 
                   }else{
                    setProducts(response.data.productInfo)
                   }
                    setPostSize(response.data.postSize)
                }else{
                    alert("상품들을 가져오는데 실패 했습니다.")
                }
            })
    }
    //더보기 버튼 이벤트
     const loadMoreHandler = () => {
        //더보기 버튼 누르면 0 + 8
        let skip = Skip + Limit

        //  loadMore:true  더보기 눌렀을때 정보 추가하여 보내기
        let body = {
            skip:skip,
            limit:Limit,
            loadMore:true
        }
        //
        getProducts(body)
        //증가되는 skip setState
        setSkip(skip)
    }

    const renderCards = Products.map((product,index) => {
        //console.log('product',product)
           return  <Col lg={6} md={8} xs={24} key={index}>     
                    <Card
                        cover={<ImageSlider images={product.images} />}
                        >
                        <Meta
                            title={product.title}
                            description={`$${product.price}`}
                        ></Meta>
                    </Card>
                </Col>
    })  

    const showFilteredResults = (filters)  => {

        let body = {
            //db 에서 처음으로 값 가져오기 skip:0, Limit은 초기화 값 8 그대로
            skip:0,
            limit:Limit,
            filters:filters

        }
        //더보기 클릭시와 같이 관련값(upload된 것중) 가져오기 
        getProducts(body)
        setSkip(0)
    }


    // checked된 1,2, _id 가 filters에 담김
    const handleFilters = (filters,category)  =>{
        //모든 Filters를 newFilters로 복제
        const newFilters ={...Filters}

        // continents 나 price 배열을 가리킴, filters = _id값 [1,2,..] 
        newFilters[category]  = filters

        showFilteredResults(newFilters)
    }


    return (

        <div style={{width:'75%', margin:'3rem auto'}} > 
            <div style={{textAlign:'center'}}>
                    <h2> REACT로 쇼핑몰만들기<Icon type="rocket"></Icon> </h2>

            </div>

                {/* Filter */}

                {/* 1) CheckBox  list이름으로 continents 데이터 checkbox component에 내려주기, list =props */}
                <CheckBox list ={continents} handleFilters={filters => handleFilters(filters,"continents")} />



                {/* RadioBox */}
                {/* Search */}

                {/* Cards 16 좌우간견 16 아래 간격 */}
                <Row gutter={[16,16]}>
                    {renderCards}
                </Row>
                <br/>

                {PostSize >= Limit &&
                    <div style={{display:'flex', justifyContent:'center'}}>
                       <button onClick={loadMoreHandler}>더보기</button>
                     </div>
                }



        </div>

    )
}

export default LandingPage
