import React,{useState} from 'react'
import{Button,Form,Input} from 'antd';
import FileUpload from '../../utils/FileUpload';


const {TextArea} = Input;
const Continents =[
  {key:1, value:'Africa'},
  {key:2, value:'Europe'},
  {key:3, value:'Asia'},
  {key:4, value:'Korea'},
  {key:5, value:'Japan'},
  {key:6, value:'America'},
  {key:7, value:'Austria'}
]

function UploadProductPage() {

  const [Title,setTitle] = useState("")
  const [Description, setDescription] = useState("")
  const [Price, setPrice] = useState(0)
  const [Continent, setContinent] = useState(1)
  const [Images,setImages] = useState([])

  const titleChangeHandler = (event) => {
    setTitle(event.currentTarget.value)
  }
  const descriptionChangeHandler = (event) => {
    setDescription(event.currentTarget.value)
  }
  const priceChangeHandler = (event) => {
    setPrice(event.currentTarget.value)
  }
  const continentsChangeHandler = (event) => {
    setContinent(event.currentTarget.value)
  }
  const updateImages =(newImages) => {   //FileUpload 에서 변경된 newImages이자 넣어 updateImages
    setImages(newImages)    //위의 state의 Images를 newImages로 setImages
  } 
  submitHandler = (event) => {
    event.preventDefault();
    if(!Title || !Description || !Price || !Continent || !Images){
      return alert("모든 값을 넣어 주셔야합니다.")
    }
  }
 
  return (
    <div style={{maxWidth:'700px', margin:'2rem auto'}}>
      <div style={{textAlign:'center',marginBottom:'2rem'}}>
        <h2>여행상품 업로드</h2>
      </div>

       <Form onSubmit={submitHandler}> {/* event 발생 */}
          {/* DropZone */}

          <FileUpload refreshFunction={updateImages}></FileUpload>
          {/* FileUpload 의 이미지 변경 내용을 갖고 오기위해 props 주기 =>  */}
          <br/>
          <br/>
          <label>이름</label>
          <Input onChange={titleChangeHandler} value={Title} ></Input>
          <br/>
          <br/>
          <label>설명</label>
          <TextArea onChange = {descriptionChangeHandler} value ={Description} ></TextArea>
          <br/>
          <br/>
          <label>가격($)</label>
          <Input type="number" onChange = {priceChangeHandler} value={Price} ></Input>
          <br/>
          <br/>
          <select onChange = { continentsChangeHandler} value={Continent}>
            {Continents.map(item => (
               <option key={item.key} value={item.key}>{item.value}</option>
            ))}
           

          </select>
          <br/>
          <br/>
          <Button type="submit" value="">
            확인
          </Button>

      </Form>
      
    </div>
  )
}

export default UploadProductPage
