import React, { useState } from 'react'
import Dropzone from 'react-dropzone'
import {Icon} from 'antd';
import axios from 'axios';  //bakend 에 파일전달 라이브러리

function FileUpload() {   //저장된 파일을 프런트엔드에 전달해주는 기능
  const [Images,setImages] = useState([])
  const dropHandler =(files) =>{
     let formData = new FormData();
    const config ={
      header:{'content-type': 'multipart/form-data'} //backend 에 request시 형식 알리기
    }
    formData.append("file",files[0])
    axios.post('/api/product/image',formData,config)  //백엔드에 file 보낼때 error 방지 위해 formData,config 인자 사용
      .then(response => {
          if(response.data.success){
            //alert('파일을 저장하는데 성공했습니다.')
            console.log(response.data)
            setImages([...Images,response.data.filePath])   // ...Images  spread operator를 이용 모든 image 저장
          }else{
            alert('파일을 저장하는데 실패했습니다.')
          }
        })
      }  //end of dropHandler

  return (
        //  {/* npm install react-dropzone --save */}
    <div style={{display:'flex', justifyContent:'spacebetween'}}>
        
      <Dropzone onDrop={dropHandler}>
        {({getRootProps, getInputProps}) => (
          <section>
            <div  style={{
                  width:300, height:240, border:'1px solid lightgray',
                  display:'flex', alignItems:'center', justifyContent:'center'
            }}
            
              {...getRootProps()}>
              <input {...getInputProps()} />
              <Icon type="plus" style={{fontSize:'3rem'}} ></Icon>
            </div>
          </section>
        )}
      </Dropzone>

            {/* //파일 렌더링  */}

            <div style={{display:'flex', width:'350px',height:'240px',overflow:'scroll', border:'1px solid blue', marginLeft:'1rem'}}>
              {Images.map((image,index) => (   //map() 메소드
                  <div key={index}> 
                    <img style={{minWidth:'300px',width:'300px',height:'240px'}}
                        src={`http://localhost:5000/${image}`} />
                  </div>
              ) )}

            </div>
    </div>
  )
}

export default FileUpload
