import {
    LOGIN_USER,
    REGISTER_USER,
    AUTH_USER,
    LOGOUT_USER,
    ADD_TO_CART,
    GET_CART_ITEMS
} from '../_actions/types';



export default function(state={},action){
    switch(action.type){
        case REGISTER_USER:
            return {...state, register: action.payload }
        case LOGIN_USER:
            return { ...state, loginSucces: action.payload }
        case AUTH_USER:
            return {...state, userData: action.payload }
        case LOGOUT_USER:
            return {...state }
        case ADD_TO_CART:
              return {...state,
                        userData:{
                             ...state.userData,  
                             //원래있던 useData넣기
                             cart:action.payload  
                            //cart넣기, users.js의 userInfo,cart <= res.status(400).send(userInfo.cart)
               }
            }
        case GET_CART_ITEMS:
            // user_actions.js 의 return response.data가 cartDetail:action.payload로 들어오게 됨
            return {...state,cartDetail:action.payload}
        default:
            return state;
    }
}
